import React, { useState } from 'react';
import { Route, Routes } from 'react-router-dom';
import Header from './header/Header';
import Main from './main/Main';
import Footer from './footer/Footer';
import { cards } from './constants/constants';

function App() {

  const [favCards, setFavCards] = useState([]);
  const [storeCards, setStoreCards] = useState([]);

  const handleAddFav = (card) => {
    setFavCards([...favCards, card]);
    console.log(favCards)
  }
  function handleAddStore(card) {
    setStoreCards([...storeCards, card]);
    console.log(storeCards)
  }

  return (
    <>
      <Header />
      <Routes>
        <Route path='/' element={<Main cards={cards}
          handleAddFav={handleAddFav}
          handleAddStore={handleAddStore} />} />
        <Route path='/favourites' element={<Main cards={favCards} />} />
        <Route path='/shopping-cart' element={<Main cards={storeCards} />} />
      </Routes>
      <Footer />
    </>
  );
}

export default App;
