import React, { useEffect } from 'react';
import "./Main.css";
import Card from './card/Card';

export default function Main({ cards, hendlerFav }) {
    let resArr = [];
    function addId(arr) {
        return arr.map((el) => {
            const _id = Math.random().toString(16).slice(2)
            return el = { ...el, id: _id }
        })
    }

    useEffect(() => {
        resArr = addId(cards);
    }, resArr)

    return (
        <main className='main'>
            <ul className='main__card-list'>
                {resArr.map((card) => {
                    return <Card
                        key={card._id}
                        data={card}
                        hendlerFav={hendlerFav} />
                })}
            </ul>
        </main>
    )
}
