import React, { useEffect, useState } from 'react';
import "./Main.css";
import Card from './card/Card';

export default function Main({ cards, hendlerFav }) {
    const [resArr, setIsResArr] = useState([])

    function addId(arr) {
        return arr.map((el) => {
            const _id = Math.random().toString(16).slice(2)
            return el = { ...el, id: _id }
        })
    }

    useEffect(() => {
        setIsResArr(addId(cards));
    }, resArr)

    return (
        <main className='main'>
            <ul className='main__card-list'>
                {resArr.length === 0 ?
                    <h2>Ничего не добавлено </h2> :
                    resArr.map((card) => {
                        return <Card
                            key={card._id}
                            data={card}
                            hendlerFav={hendlerFav} />
                    })}
            </ul>
        </main>
    )
}
